import 'dart:io';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:mt_gilead/pages/store/CheckoutFlowPoint.dart';class CheckoutFlowPage extends StatelessWidget {  Widget _buildCrumbLine() {    return Positioned(        top: 0.0,        bottom: 0.0,        left: 24.0,        child: Container(          width: 2.0,          color: Colors.grey[300],        ));  }  @override  Widget build(BuildContext context) {    return Platform.isAndroid        ? Scaffold(            body: SafeArea(              child: Stack(                children: <Widget>[                  _buildCrumbLine(),                  CheckoutFlowPoint(title: "Summary", color: Colors.amber),                  SizedBox(height: 24.0),                  CheckoutFlowPoint(title: "Checkout", color: Colors.green),                ],              ),            ),          )        : CupertinoPageScaffold(            child: SafeArea(              child: Stack(                children: <Widget>[                  _buildCrumbLine(),                  CheckoutFlowPoint(title: "Summary", color: Colors.amber),                  CheckoutFlowPoint(title: "Checkout", color: Colors.green),                ],              ),            ),          );  }}