import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class CheckoutFlowPoint extends StatelessWidget {  final String title;  final Color color;  CheckoutFlowPoint({@required this.title, @required this.color});  final double dotSize = 18.0;  Widget _buildDot(Color c) {    return Padding(      padding: EdgeInsets.symmetric(horizontal: 25.0 - dotSize / 2),      child: Container(        height: dotSize,        width: dotSize,        decoration: BoxDecoration(shape: BoxShape.circle, color: c),      ),    );  }  Widget _buildTitle(String t) {    return Expanded(      child: Text(        t,        style: TextStyle(fontSize: 18.0, color: Colors.grey),      ),    );  }  @override  Widget build(BuildContext context) {    // TODO: implement build    return Padding(      padding: const EdgeInsets.symmetric(vertical: 16.0),      child: Row(        children: <Widget>[_buildDot(color), _buildTitle(title)],      ),    );  }}